import { VStack, Image, Text, Button } from "native-base";
import { LinearGradient } from 'expo-linear-gradient';
import BackgroundDateImg from '@assets/BackgroundDateScreen.jpg'

import { useNavigation } from '@react-navigation/native';

import DateTimePicker from '@react-native-community/datetimepicker';
import AsyncStorage from '@react-native-async-storage/async-storage';


export function Date() {


  return (

    <VStack flex={1} bg="gray.700" px={22}>

      <Image
        source={BackgroundDateImg}
        alt="Music Event Image"
        maxW={435}
        maxH={323}
        resizeMode="cover"
        position="absolute"
      />

    <LinearGradient
        colors={['#6D50A0', '#E983F7', '#EC84F9', '#F687FF']}
        locations={[0, 0.85, 0.5, 1]}
        start={[0, 0]}
        end={[1, 0]}
        style={{borderRadius: 8, overflow: 'hidden' }}
        marginTop={714}

      >
        <Button
        variant="unstyled"
        padding={18}
        >
          <Text color="gray.100" fontWeight={700}>
            Explore Events
          </Text>
        </Button>
      </LinearGradient>

    </VStack>
  );
}
