import React, { useEffect, useState } from 'react';
import { Image, Text, Box, VStack, Input, Icon, Center, Button, ScrollView, Pressable, IPressableProps} from 'native-base';
import { LinearGradient } from 'expo-linear-gradient';


import BackgroundImg from '@assets/Background.png';
import Search from '@assets/search.svg';

import { useNavigation } from '@react-navigation/native';

import { useForegroundPermissions, watchPositionAsync, LocationAccuracy, LocationSubscription } from 'expo-location';

import AsyncStorage from '@react-native-async-storage/async-storage';


import { Loading } from '@components/Loading';
import { getAddressLocation } from '@utils/getAddressLocation';

export function Region() {
  const [locationForegroundPermission, requestLocationForegroundPermission] = useForegroundPermissions();
  const [isLoadingLocation, setIsLoadingLocation] = useState(true);
  const [currentAddress, setCurrentAddress] = useState<string | null>(null);
  const [inputValue, setInputValue] = useState('');
  const [selectedCity, setSelectedCity] = useState<string | null>(null);
  const navigation = useNavigation();


  useEffect(() => {
    requestLocationForegroundPermission();
  }, []);

  useEffect(() => {
    if (!locationForegroundPermission?.granted) {
      return;
    }

    let subscription: LocationSubscription;
    watchPositionAsync({
      accuracy: LocationAccuracy.High,
      timeInterval: 1000,
    }, (location) => {
      getAddressLocation(location.coords)
        .then((address) => {
          if (address) {
            setCurrentAddress(address);
          }
        })
        .finally(() => setIsLoadingLocation(false));
    }).then((response) => subscription = response);

    return () => {
      if (subscription) {
        subscription.remove();
      }
    };
  }, [locationForegroundPermission]);

  const handleSearch = async () => {
    if (inputValue.trim() === '') {
      return;
    }

    const location = await getAddressLocation({ latitude: 0, longitude: 0 });
    if (location) {
      await AsyncStorage.setItem('selectedLocation', JSON.stringify(location));

      navigation.navigate('date_selection', { selectedLocation: location });

      setSelectedCity(inputValue);
      setInputValue('');
    }
  };


  if (!locationForegroundPermission?.granted) {
    return (
      <Text color='gray.100' fontSize='lg' textAlign='center' margin={24}>
        To discover all upcoming music events, allow location access. Head to your device settings and grant Luster permission to access your location.
      </Text>
    );
  }

  if (isLoadingLocation) {
    return <Loading />;
  }

  return (

    <ScrollView contentContainerStyle={{ flexGrow: 1}} showsHorizontalScrollIndicator={false}>

      <VStack flex={1} bg="gray.700" px={22}>
      <Image
        source={BackgroundImg}
        alt="Music Event Image"
        maxW={428}
        maxH={624}
        resizeMode="cover"
        position="absolute"
      />

      <Text color='gray.100' fontSize='sm' fontWeight={400} mt={20} >
        Your Location
      </Text>
      {selectedCity ? (
        <Center>
          <Text color='gray.100' fontSize='xl'>{selectedCity}</Text>
        </Center>
      ) : (
        <Text color='gray.100' fontSize='lg' fontWeight={700} >
          {currentAddress}
        </Text>
      )}


      <Input

        px={2}
        h={14}
        mt={8}
        borderRadius={8}
        backgroundColor='transparent'
        borderWidth={.4}
        fontSize='xs'
        fontFamily='body'
        placeholder='Discover nearby events'
        color='gray.100'
        value={inputValue}
        onChangeText={(text) => setInputValue(text)}
        _focus={{
          borderWidth: 1,
          borderColor: 'purple.900',
        }}
        onSubmitEditing={handleSearch}
        InputRightElement={
       <PressableR mr={2} mt={-6} > <Icon as={<Search />} onPress={handleSearch} /></PressableR>}
      />


      <Pressable>
        <LinearGradient
          colors={['#6D50A0', '#E983F7', '#EC84F9', '#F687FF']}
          locations={[0, 0.85, 0.5, 1]}
          start={[0, 0]}
          end={[1, 0]}
          style={{borderRadius: 8, overflow: 'hidden' }}
          marginTop={494}

        >
          <Button
          variant="unstyled"

          >
            <Text color="gray.100">
              NEXT
            </Text>
          </Button>
        </LinearGradient>
      </Pressable>




    </VStack>

    </ScrollView>
  );
}
