import React from "react";
import { Image, Text, VStack, Box, HStack, ScrollView } from "native-base";
import { LinearGradient } from "expo-linear-gradient";
import LocationSvg from "@assets/Location.svg";

type Props = {
  day?: string;
  month?: string;
  eventName?: string;
  venue?: string;
  city?: string;
  country?: string;
};

export function UpcomingEvents({ day, month, eventName, venue, city, country }: Props) {
  const eventsData = [
    {
      gradientColors: ["#6D50A0", "#E983F7", "#EC84F9", "#F687FF"],
      day: "19",
      month: "JAN",
      eventName: "Apocalyptica",
      venue: "Carioca Club",
      city: "São Paulo",
      country: "Brazil",
      image: require('@assets/Apocalyptica.jpeg'),
    },
    {
      gradientColors: ["#6D50A0", "#E983F7", "#EC84F9", "#F687FF"],
      day: "2",
      month: "MAR",
      eventName: "Laura Pausini",
      venue: "Teatro Renault",
      city: "São Paulo",
      country: "Brazil",
      image: require('@assets/laurapausini2.jpeg'),
    },
    {
      gradientColors: ["#6D50A0", "#E983F7", "#EC84F9", "#F687FF"],
      day: "1",
      month: "MAY",
      eventName: "Accept",
      venue: "Carioca Club",
      city: "São Paulo",
      country: "Brazil",
      image: require('@assets/accept.jpeg'),
    }
  ];

  return (

    <ScrollView showsHorizontalScrollIndicator={false} >
      <VStack mt={12} >
      {eventsData.map((event, index) => (
        <LinearGradient
          key={index}
          colors={event.gradientColors}
          locations={[0, 0.85, 0.5, 1]}
          start={[0, 0]}
          end={[1, 0]}
          width={390}
          height={103}
          borderRadius={6}
          mb={4}
          marginLeft={3}
        >
          <HStack space={4} width="100%" alignItems="center" px={4} py={2}>
            <Box width={78} height={78} borderRadius={8} overflow="hidden">
              <Image
                source={event.image}
                style={{
                  flex: 1,
                  width: null,
                  height: null,
                  borderRadius: 8,
                  overflow: "hidden",
                }}
                resizeMode="cover"
                alt={`Event Image ${index}`}
              />
            </Box>
            <VStack alignItems="flex-start" flex={1}>
              <Text color="white" fontSize="lg" fontWeight="bold" textAlign="left">
                {event.day}
              </Text>
              <Text color="white" fontSize="sm" textAlign="left">
                {event.month}
              </Text>
              <Text color="white" fontSize="md" fontWeight="bold" textAlign="left">
                {event.eventName}
              </Text>
              <HStack space={2} alignItems="center">
                <LocationSvg width={16} height={16} fill="white" />
                <Text color="white" fontSize="xs" textAlign="left">
                  {`${event.venue}, ${event.city} - ${event.country}`}
                </Text>
              </HStack>
            </VStack>
          </HStack>
        </LinearGradient>
      ))}
    </VStack>
    </ScrollView>

  );
}
