import React from "react";
import { Image, Text, VStack, Box, HStack, ScrollView, Pressable, IPressableProps} from "native-base";
import { LinearGradient } from "expo-linear-gradient";
import LocationSvg from "@assets/location.svg";
import ArrowSvg from '@assets/Arrow.svg'
import { useNavigation } from '@react-navigation/native';


type Props = {
  day?: string;
  month?: string;
  eventName?: string;
  venue?: string;
  city?: string;
  country?: string;
};

export function UpcomingEvents({ day, month, eventName, venue, city, country }: Props) {
  const eventsData = [
    {
      gradientColors: ["#6D50A0", "#E983F7", "#EC84F9", "#F687FF"],
      day: "19",
      month: "JAN",
      eventName: "Apocalyptica",
      venue: "Carioca Club",
      city: "São Paulo",
      country: "Brazil",
      image: require('@assets/Apocalyptica.jpeg'),
    },
    {
      gradientColors: ["#6D50A0", "#E983F7", "#EC84F9", "#F687FF"],
      day: "2",
      month: "MAR",
      eventName: "Laura Pausini",
      venue: "Teatro Renault",
      city: "São Paulo",
      country: "Brazil",
      image: require('@assets/laurapausini2.jpeg'),
    },
    {
      gradientColors: ["#6D50A0", "#E983F7", "#EC84F9", "#F687FF"],
      day: "1",
      month: "MAY",
      eventName: "Accept",
      venue: "Carioca Club",
      city: "São Paulo",
      country: "Brazil",
      image: require('@assets/accept.jpeg'),
    }
  ];

  const navigation = useNavigation();

  return (

    <ScrollView showsHorizontalScrollIndicator={false} >
      <Pressable
         onPress={() => {
          navigation.navigate('event_details');
        }}
      >

        <VStack mt={4}>
        {eventsData.map((event, index) => (
          <Box borderRadius={128}>

          <LinearGradient
            key={index}
            colors={event.gradientColors}
            locations={[0, 0.85, 0.5, 1]}
            start={[0, 0]}
            end={[1, 0]}
            width={390}
            height={110}
            mb={4}
            marginLeft={3}
            marginRight={3}
            marginBottom={16}
            borderRadius={6}


          >
            <HStack space={4} width="100%" alignItems="center" px={4} py={2}>
              <Box width={94} height={94} borderRadius={8} overflow="hidden">
                <Image
                  source={event.image}
                  style={{
                    flex: 1,
                    width: null,
                    height: null,
                    borderRadius: 8,
                    overflow: "hidden",
                  }}
                  resizeMode="cover"
                  alt={`Event Image ${index}`}
                />
              </Box>
              <VStack alignItems="flex-start" flex={1} >
                <Text color='gray.100' fontSize="lg" fontWeight="bold" textAlign="left" lineHeight={20}>
                  {event.day}
                </Text>
                <Text color='gray.100' fontSize="sm" textAlign="left" >
                  {event.month}
                </Text>
                <Text color='gray.100' fontSize="md" fontWeight="bold" textAlign="left">
                  {event.eventName}
                </Text>
                <HStack space={2} alignItems="center">
                  <LocationSvg width={10} height={10} fill="white" />
                  <Text color='#F9FAFC' fontSize="xs" textAlign="left">
                    {`${event.venue}, ${event.city} - ${event.country}`}
                  </Text>
                </HStack>
                <ArrowSvg width={24} height={24} fill="#FFFFFF" position="absolute" top={36} right={18} />
              </VStack>
            </HStack>
          </LinearGradient>

          </Box>
        ))}
        </VStack>

      </Pressable>

    </ScrollView>

  );
}
